ruleToc.chapterList:
.chapterlist dd a
@js:
list = result.toArray();
chapter = [];
for (i in list) {
    name = list[i].text();
    href = list[i].attr('href')
    if (href == '/') {
        url = list[i - 1].attr('href');
        num = Number(url.match(/\d+/));
        href = num + 1 + '.html'
    }
    chapter.push({
        text: String(name).replace(/^(\d+)、第.+章/,'第$1章').replace(/^(\d+)、\d+、/,'第$1章 ').replace(/^(\d+)、\d+/,'第$1章').replace(/^(第.+章)\s?\d+/,'$1').replace(/^(\d+)、/,'第$1章 ').replace(/^(第.+章)\s?第.+章/,'$1').replace(/第\s(.+)\s章/,'第$1章').replace(/.*(chapter|Chapter)\s?(\d+)\s?/,'$1 $2 ').replace(/\(.+\)/,'').replace(/……|。/,'').replace(/“.+/,'').replace(/(）).+/,'$1').replace(/第([零一二三四五六七八九十百千]+)章/g,java.toNumChapter(result)),
        href: baseUrl + href + ",{'webView': true}"
    })
}
chapter;

