js:
var d = [];
//隐藏设置
const 轮娌片 = 1
let true_url = getVar("mlk.url", MY_URL)
const page = MY_URL.match(/addtime-(.*?)\.html/)[1]
true_url = true_url.replace('e.html', 'e-'+page + '.html')
const html = fetch(true_url, {
    headers: {
        'User-Agent': 'Mozilla/5.0 (Windows NT 10.0)'
    }
})
const empty = "hiker://empty"

const categories = parseDomForArray(html, 'dl&&dd').concat(parseDomForArray(html, 'body&&.btn-group'));

let init_cate = []

for (let i = 0; i < 20; i++) {
    init_cate.push("0")
}

const fold = getVar("mlk.fold", "0")
const cate_temp_json = getVar("mlk.category", JSON.stringify(init_cate))
const cate_temp = JSON.parse(cate_temp_json)


if (parseInt(page) === 1) {
    d.push({
        title: fold === '1' ? '““””<b><span style="color: #FF0000">∨</span></b>' : '““””<b><span style="color: #1aad19">∧</span></b>',
        url: $(empty).lazyRule((fold) => {
            putVar("mlk.fold", fold === '1' ? '0' : '1');
            refreshPage(false);
            return "hiker://empty"
        }, fold),
        col_type: 'scroll_button',
    })


    categories.forEach((category, index) => {
        let sub_categories = parseDomForArray(category, 'body&&a');
        if (index === 0) {
            sub_categories.forEach((item, key) => {
                let title = parseDomForHtml(item, 'a&&Text')
 if (title !== '轮娌片' || 轮娌片 === 1) {
                d.push({
                    title: key.toString() === cate_temp[index] ? '““””<b><span style="color: #098AC1">' + title + '</span></b>' : title,
                    url: $(parseDom(item, 'a&&href')).lazyRule((params) => {
                        let new_cate = []
                        params.cate_temp.forEach((cate, index) => {
                            new_cate.push(index === 0 ? params.key.toString() : "0")
                        })
                        putVar("mlk.category", JSON.stringify(new_cate))
                        putVar("mlk.url", input)
                        refreshPage(true)
                        return "hiker://empty"
                    }, {
                        cate_temp: cate_temp,
                        key: key,
                        page: page,
                    }),
                    col_type: 'scroll_button',
                })
              }
            })
            d.push({
                col_type: "blank_block"
            });
        } else if (fold === '1') {
            sub_categories.forEach((item, key) => {
                let title = parseDomForHtml(item, 'a&&Text')
                d.push({
                    title: key.toString() === cate_temp[index] ? '““””<b><span style="color: #098AC1">' + title + '</span></b>' : title,
                    url: $(parseDom(item, 'a&&href')).lazyRule((params) => {
                        params.cate_temp[params.index] = params.key.toString()

                        putVar("mlk.category", JSON.stringify(params.cate_temp))
                        putVar("mlk.url", input)
                        refreshPage(true)
                        return "hiker://empty"
                    }, {
                        cate_temp: cate_temp,
                        index: index,
                        key: key,
                        page: page,
                    }),
                    col_type: 'scroll_button',
                })
            })
            d.push({
                col_type: "blank_block"
            });
        }
    })
}
const list = parseDomForArray(html, '.ff-img-215&&li')
list.forEach(video => {
    d.push({
        title: parseDomForHtml(video, 'h2&&Text'),
        desc: parseDomForHtml(video, '.continu&&Text'),
        pic_url: parseDom(video, '.img-thumbnail&&data-original'),
        url: parseDom(video, 'a&&href') + '#immersiveTheme#',
        col_type: 'movie_3_marquee',
    })
})
setResult(d);

