var timeou = 1500;

var ss = MY_URL.split('$$$')[1];
var num= MY_URL.split('$$$')[2];
var le = num*ssxc;
//setError(le);
if(num=='1'){
putVar({key:'zyfany', value:''});

if(getVar('jsmod')=='0'){
var erj=fetch('https://gitee.com/zonb/hiker/raw/master/zywck.js',{});
writeFile('hiker://files/rules/zywcj.js', erj)
}
//设置搜索远程文件地址
